namespace arter\amos\slideshow\assets;

use yii\web\AssetBundle;

/**
 * Class ModuleSlideshowAsset
 * @package arter\amos\slideshow\assets
 */
class ModuleSlideshowAsset extends AssetBundle
{
    public $sourcePath = '@vendor/arter/amos-slideshow/src/assets/web';

    public $css = [
        'less/slideshow.less'
    ];
    public $js = [        
    ];
    public $depends = [
    ];

    public function init()
    {
        $moduleL = \Yii::$app->getModule('layout');
        if(!empty($moduleL))
        { $this->depends [] = 'arter\amos\layout\assets\BaseAsset'; }
        else
        { $this->depends [] = 'arter\amos\core\views\assets\AmosCoreAsset'; }
        parent::init(); // TODO: Change the autogenerated stub
    }
}
